import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import ProductList from './components/ProductList';
import ShoppingCart from './components/ShoppingCart';
import Footer from './components/Footer'; 
import HomePage from './components/HomePage';
import BackgroundImage from './components/BackgroundImage';
import ContactPage from './components/ContactPage';
import SearchBar from './components/SearchBar'; // Om du har en separat komponent för sökningen

// Importera dina bilder
import produkt1 from './Bilder/produkt1.png';
import produkt2 from './Bilder/produkt2.png';
import produkt3 from './Bilder/produkt3.png';
import produkt4 from './Bilder/produkt4.png';
import produkt5 from './Bilder/produkt5.png';
import produkt6 from './Bilder/produkt6.png';

import './style.css';
import PropTypes from 'prop-types';

function App() {
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('lowest');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/products'); 
        setProducts(response.data);
      } catch (error) {
        setError('Det gick inte att hämta produkterna');
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = product => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = index => {
    if (index === -1) setCart([]); // Clear the entire cart
    else setCart(cart.filter((_, i) => i !== index));
  };

  const handleCheckout = () => {
    alert('Köp genomfört!');
    setCart([]); // Clear the cart after purchase
  };

  const handleSearch = term => {
    setSearchTerm(term.toLowerCase());
  };

  const handleSort = order => {
    setSortOrder(order);
  };

  const filteredAndSortedProducts = products
    .filter(product => 
      product.name.toLowerCase().includes(searchTerm) ||
      product.description.toLowerCase().includes(searchTerm)
    )
    .sort((a, b) => {
      if (sortOrder === 'lowest') {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    });

  return (
    <Router>
      <Header cartCount={cart.length} onSearch={handleSearch} onSort={handleSort} />
      <div className="container mt-4" style={{ minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={error ? <p>{error}</p> : <HomePage products={filteredAndSortedProducts} onAddToCart={handleAddToCart} />} />
          <Route path="/produkter" element={error ? <p>{error}</p> : <ProductList products={filteredAndSortedProducts} onAddToCart={handleAddToCart} />} />
          <Route path="/kundvagn" element={<ShoppingCart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />} />
          <Route path="/kontakt" element={<ContactPage />} />
        </Routes>
      </div>
      <BackgroundImage />
      <Footer />
    </Router>
  );
}

App.propTypes = {
  products: PropTypes.array,
  cart: PropTypes.array,
  error: PropTypes.string,
  handleAddToCart: PropTypes.func,
  handleRemoveFromCart: PropTypes.func,
  handleCheckout: PropTypes.func,
};

export default App;
