import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

import './Product.css';

function Product({ product, onAddToCart }) {
  const [productInfo, setProductInfo] = useState(product);
  const [addingToCart, setAddingToCart] = useState(false);
  const imageUrl = productInfo.image || 'standard-bild-url';

  useEffect(() => {
    setProductInfo(product);
  }, [product]);

  const addToCart = async () => {
    try {
      setAddingToCart(true);
      await axios.post('http://localhost:3000/update-inventory', {
        productId: productInfo.id,
        quantity: 1,
      });
      const response = await axios.get(`http://localhost:3000/products/${productInfo.id}`);
      setProductInfo({ ...productInfo, stock: response.data.stock });
      onAddToCart(productInfo);
      setAddingToCart(false);
    } catch (error) {
      console.error('Error adding to cart:', error);
      setAddingToCart(false);
    }
  };

  return (
    <div className="card h-100">
      <img
        src={imageUrl}
        alt={productInfo.name}
        className="card-img-top"
        onError={(e) => {
          e.target.src = 'fallback-image-url';
          e.target.alt = 'Bild saknas';
        }}
      />
      {productInfo.isBestseller && (
        <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-success">
          Bästsäljare
        </span>
      )}
      <div className="card-body d-flex flex-column">
        <h5 className="card-title">{productInfo.name}</h5>
        <p className="card-text flex-grow-1">{productInfo.description}</p>
        <p className="card-text">
          <strong>{productInfo.price} kr</strong>
        </p>
        {productInfo.stock > 0 ? (
          <div>
            <p className="card-text">
              Antal i lager: {productInfo.stock}
            </p>
            <button
              onClick={addToCart}
              className="btn btn-primary mt-2"
              disabled={addingToCart}
            >
              {addingToCart ? 'Lägger till...' : 'Lägg till i kundvagn'}
            </button>
          </div>
        ) : (
          <div>
            <p className="card-text text-danger">
              Produkten är tyvärr slut i lager.
            </p>
            <button
              className="btn btn-secondary mt-2"
              disabled={true}
            >
              Lägg till i kundvagn
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

Product.propTypes = {
  product: PropTypes.shape({
    id: PropTypes.number.isRequired,
    image: PropTypes.string,
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    isBestseller: PropTypes.bool,
    stock: PropTypes.number.isRequired,
  }).isRequired,
  onAddToCart: PropTypes.func.isRequired,
};

export default Product;
