import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Product from './Product';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/products');
        // Lägg till en bild-URL om ingen finns
        const productsWithImages = response.data.map(product => ({
          ...product,
          image: product.image || 'sökväg/till/din/standardbild.jpg', // Ange sökväg till din standardbild här
        }));
        setProducts(productsWithImages);
      } catch (error) {
        setError('Det gick inte att hämta produkterna');
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    // Implementera lägg till i kundvagn logiken här
    console.log('Produkt tillagd i kundvagnen:', product);
  };

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="container">
      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <Product product={product} onAddToCart={handleAddToCart} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
