import React from 'react';
import Product from './Product';

function Bestsellers({ products, onAddToCart }) {
  const bestsellers = products.filter(product => product.isBestseller);

  const textStyle = {
    backgroundColor: 'rgba(255, 255, 255, 0.4)', // Vit färg med 0.8 opacitet
    padding: '5px 10px', // Lite utrymme runt texten
    borderRadius: '2px', // Rundade hörn
  };

  return (
    <div>
      <h2 style={{ textAlign: 'center', marginTop: '20px' }}>  {/* Lägg till marginTop här */}
        <span style={textStyle}>Våra 3 Bästsäljare:</span>
      </h2>
      <div className="row">
        {bestsellers.map(product => (
          <div key={product.id} className="col-md-4">
            <Product product={product} onAddToCart={() => onAddToCart(product)} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Bestsellers;
