import React from 'react';
import Product from './Product';

function Bestsellers({ products, onAddToCart }) {
  const bestsellers = products.filter(product => product.isBestseller);

  const headerStyle = {
    backgroundColor: '#f5f5dc', // Ljusbrun färg
    textAlign: 'center',
    padding: '10px',
    borderRadius: '5px' // Rundade hörn
  };

  return (
    <div>
      <h2 style={headerStyle}>Vår Bästsäljare</h2>
      <div className="row">
        {bestsellers.map(product => (
          <div key={product.id} className="col-md-4">
            <Product product={product} onAddToCart={() => onAddToCart(product)} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Bestsellers;
