import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Product from './Product';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:3000/products'); // Byt ut mot din backend-URL
        setProducts(response.data);
      } catch (error) {
        setError('Det gick inte att hämta produkterna');
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    // Implementera lägg till i kundvagn logiken här
    console.log('Produkt tillagd i kundvagnen:', product);
  };

  return (
    <div className="container">
      <div className="row">
        {error ? (
          <p>{error}</p>
        ) : (
          products.map((product) => (
            <div key={product.id} className="col-md-4 mb-4">
              <Product product={product} onAddToCart={handleAddToCart} />
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ProductList;
