function App() {
  // ... (andra states och funktioner)

  const handleRemoveFromCart = (index) => {
    if (index === -1) { // Om index är -1, töm hela varukorgen
      setCart([]);
    } else { // Annars, ta bort produkten på det specifika indexet
      const newCart = [...cart];
      newCart.splice(index, 1);
      setCart(newCart);
    }
  };

  const handleCheckout = () => {
    alert('Tack för ditt köp!');
    setCart([]); // Töm varukorgen efter köp
  };

  return (
    <Router>
      <Header cartCount={cart.length} />
      <div className="container mt-4" style={{ minHeight: '100vh' }}>
        <Routes>
          {/* ... (andra routes) */}
          <Route path="/kundvagn" element={<ShoppingCart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />} />
        </Routes>
      </div>
      <BackgroundImage />
      <Footer />
    </Router>
  );
}
