import React, { useState } from 'react';
import PropTypes from 'prop-types';

function ProductListItem({ product, onAddToCart }) {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    // Skicka en förfrågan till backend för att uppdatera lagerantalet.
    // Om förfrågan lyckas, kör onAddToCart med produkten och antalet.
    // Om förfrågan misslyckas, visa ett felmeddelande eller hantera det på lämpligt sätt.
  };

  return (
    <div>
      <h3>{product.name}</h3>
      <p>{product.description}</p>
      <p>Pris: {product.price} SEK</p>
      <input
        type="number"
        value={quantity}
        onChange={(e) => setQuantity(e.target.value)}
      />
      <button onClick={handleAddToCart}>Lägg till i kundvagn</button>
    </div>
  );
}

ProductListItem.propTypes = {
  product: PropTypes.shape({
    name: PropTypes.string.isRequired,
    description: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    stock: PropTypes.number.isRequired, // Lägg till lager som en egenskap i produkten
  }).isRequired,
  onAddToCart: PropTypes.func.isRequired,
};

export default ProductListItem;
