import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './components/Header';
import ProductList from './components/ProductList';
import ShoppingCart from './components/ShoppingCart';
import Footer from './components/Footer'; 
import HomePage from './components/HomePage';
import BackgroundImage from './components/BackgroundImage';
import './style.css';

function App() {
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: 'Produkt 1', price: 100, isBestseller: true },
    { id: 2, name: 'Produkt 2', price: 150, isBestseller: true },
    { id: 3, name: 'Produkt 3', price: 200, isBestseller: true },
    { id: 4, name: 'Produkt 4', price: 250, isBestseller: false },
    { id: 5, name: 'Produkt 5', price: 300, isBestseller: false },
    { id: 6, name: 'Produkt 6', price: 350, isBestseller: false },
  ];

  const handleAddToCart = product => {
    setCart([...cart, product]);
  };

  const handleRemoveFromCart = index => {
    if (index === -1) setCart([]); // Clear the entire cart
    else setCart(cart.filter((_, i) => i !== index));
  };

  const handleCheckout = () => {
    alert('Köp genomfört!');
    setCart([]); // Clear the cart after purchase
  };

  return (
    <Router>
      <Header cartCount={cart.length} />
      <div className="container mt-4" style={{ minHeight: '100vh' }}>
        <Routes>
          <Route path="/" element={<HomePage products={products} onAddToCart={handleAddToCart} />} />
          <Route path="/produkter" element={<ProductList products={products} onAddToCart={handleAddToCart} />} />
          <Route path="/kundvagn" element={<ShoppingCart cart={cart} onRemoveFromCart={handleRemoveFromCart} onCheckout={handleCheckout} />} />
          <Route path="/kontakt" element={<h1>Kontaktsida</h1>} />
          {/* Lägg till fler Routes här om du vill */}
        </Routes>
      </div>
      <div style={{ minHeight: '200px' }}>
        {/* Extra utrymme för att möjliggöra scrolling */}
      </div>
      <BackgroundImage />
      <Footer />
    </Router>
  );
}

export default App;
